package week1Day2;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int input=59;
		
		if((input%2)==0)
		{
			
			System.out.println("The input is a even number");
		
		}else
			
			System.out.println("The input is a odd number");
		
	} 

	}


